import { Component } from '@angular/core';
import { Contact } from '../models/contact.model'; // adjust path as per your project structure
import { ContactService } from '../contact.service';

@Component({
  selector: 'app-contact-add',
  templateUrl: './contact-add.component.html',
  styleUrls: ['./contact-add.component.css']
})
export class ContactAddComponent {
  contact: Contact = { id: 0, name: '', email: '', phone: '' }; // initialize with default values

  constructor(private contactService: ContactService) {}

  addContact(): void {
    this.contactService.addContact(this.contact).subscribe(
      () => {
        // handle success, e.g., show a success message or navigate to contact list
      },
      error => {
        // handle error
        console.error('Error adding contact:', error);
      }
    );
  }
}
