import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Contact } from '../models/contact'; // Adjust path if necessary
import { ContactService } from '../services/contact.service'; // Adjust path if necessary

@Component({
  selector: 'app-contact-edit',
  templateUrl: './contact-edit.component.html',
  styleUrls: ['./contact-edit.component.css']
})
export class ContactEditComponent implements OnInit {
  contact: Contact;
  contactId: number; // Ensure contactId is of type number

  constructor(
    private route: ActivatedRoute,
    private contactService: ContactService
  ) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      this.contactId = +params.get('id'); // Convert to number using +
      this.loadContact();
    });
  }

  loadContact(): void {
    this.contactService.getContact(this.contactId).subscribe(
      (data: Contact) => {
        this.contact = data;
      },
      (error) => {
        console.error('Error fetching contact:', error);
      }
    );
  }

  // Other methods for updating contact
}
<h2>Edit Contact</h2>
<form (ngSubmit)="updateContact()">
  <label>Name:</label>
  <input [(ngModel)]="contact.name" name="name" required>
  <label>Email:</label>
  <input [(ngModel)]="contact.email" name="email" required>
  <label>Phone:</label>
  <input [(ngModel)]="contact.phone" name="phone" required>
  <button type="submit">Update</button>
</form>
